--
-- Table structure for table `question`
--

CREATE TABLE IF NOT EXISTS `question` (
  `q_no` int(40) NOT NULL,
  `q_english` varchar(1000) NOT NULL,
  `q_name` varchar(1000) CHARACTER SET utf8 NOT NULL,
  `option_1` varchar(500) CHARACTER SET utf8 NOT NULL,
  `english_option_1` varchar(500) NOT NULL,
  `option_2` varchar(500) CHARACTER SET utf8 NOT NULL,
  `english_option_2` varchar(500) NOT NULL,
  `option_3` varchar(500) CHARACTER SET utf8 NOT NULL,
  `english_option_3` varchar(500) NOT NULL,
  `option_4` varchar(500) CHARACTER SET utf8 NOT NULL,
  `english_option_4` varchar(500) NOT NULL,
  `passage_id` int(11) NOT NULL,
  `answer_correct` int(11) NOT NULL DEFAULT '1',
  PRIMARY KEY (`q_no`),
  KEY `passage_id` (`passage_id`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1;

